<?php $msg=$_POST['AdminComm'];$commtype=$_POST['CommType'];if($commtype==="Notification"){$query='SELECT Email FROM `users` WHERE WantsEmail = 1';require"connect.php";try{$stmt=$pdo->prepare($query);$stmt->execute();$msg=wordwrap($msg,80);while($row=$stmt->fetch()){foreach($row as $email){mail($email,"Notification From WMDB!!!",$msg);}}echo "<h1> Notification sent successfully!</h1>";echo "<p><a href='index.html'>Return to Home Page</a></p>";}catch(Exception $e){echo "<p>Unhandled error occured while checking database.</p>";echo "<p><a href='index.html'>Return to Home Page</a></p>";return;}}elseif($commtype==="Newsletter"){$query='SELECT Email FROM `users` WHERE WantsNotification = 1';require"connect.php";try{$stmt=$pdo->prepare($query);$stmt->execute();$msg=wordwrap($msg,80);while($row=$stmt->fetch()){foreach($row as $email){mail($email,"WMDB Monthly Newsletter",$msg);}}echo "<h1> Newsletter sent successfully!</h1>";echo "<p><a href='index.html'>Return to Home Page</a></p>";}catch(Exception $e){echo "<p>Unhandled error occured while checking database.</p>";echo "<p><a href='index.html'>Return to Home Page</a></p>";return;}}else{echo "<p>There has been a problem, no communication type has been selected</p>";}?>