<h1>Movie Rating</h1>
<?php {if(empty($_POST['likeDislike'])){echo "<p>Error reading data</p>";echo "<p><a href='index.html'>Return to Home Page</a></p>";return;}$array=array();$array=explode(",",$_POST['likeDislike'],2);switch($array[0]){case 'like':$query="UPDATE `movierating` SET Likes = Likes + 1 WHERE ID = ".$array[1].";";break;case 'dislike':$query="UPDATE `movierating` SET Dislikes = Dislikes + 1 WHERE ID = ".$array[1].";";break;default:echo "<p>Bad input given</p>";echo "<p><a href='index.html'>Return to Home Page</a></p>";return;break;}require"connect.php";try{$stmt=$pdo->prepare($query);$stmt->execute();echo "<p>Rating submitted.</p>";}catch(Exception $e){echo "<p>Cannot execute rating.</p>";}echo "<p><a href='index.html'>Return to Home Page</a></p>";}?>